import { useState, useEffect } from "react";
import { StyledSelector } from "./style";

// result :string in ['low', 'medium', 'high']
// stored in localStorage (use PredictionResult.tsx)
const SetResult = () => {
	const [result, setResult] = useState<string>('low');

	useEffect(() => {
		const storedResult = localStorage.getItem('predictResult');
		if (storedResult && 
				(storedResult === 'low' || 
				 storedResult === 'medium' || 
				 storedResult === 'high')
			 ) {
			setResult(storedResult);
		}
	}, []);

	const handleChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
		const selectedValue = event.target.value;
		setResult(selectedValue);
		localStorage.setItem('predictResult', selectedValue);
		console.log(`Selected result: ${selectedValue}`);
	};

	return (
		<div className="bg-blue-500 p-4 rounded shadow-md">
			<label htmlFor="result" className="mb-2 text-lg font-bold">Select Result:</label>
			<StyledSelector id="result" value={result} onChange={handleChange}>
				<option value="low">Low</option>
				<option value="medium">Medium</option>
				<option value="high">High</option>
			</StyledSelector>
		</div>
	);
}

export default SetResult;
